					foreach (CustomDictionary item in this)
					{
						if (!String.IsNullOrEmpty(item.WordGUID) && 
							!vals.ContainsKey(item.WordGUID.Trim())) { vals.Add(item.WordGUID.Trim(), item.WordGUID.Trim()); }
					}
					break;
				case mgModel.CustomDictionaryField.Word:
					foreach (CustomDictionary item in this)
					{
						if (!String.IsNullOrEmpty(item.Word) && 
							!vals.ContainsKey(item.Word.Trim())) { vals.Add(item.Word.Trim(), item.Word.Trim()); }
					}
					break;
				case mgModel.CustomDictionaryField.AddedByUserID:
					foreach (CustomDictionary item in this)
					{
						if (!String.IsNullOrEmpty(item.AddedByUserID) && 
							!vals.ContainsKey(item.AddedByUserID.Trim())) { vals.Add(item.AddedByUserID.Trim(), item.AddedByUserID.Trim()); }
					}
					break;
				case mgModel.CustomDictionaryField.UpdatedByUserID:
					foreach (CustomDictionary item in this)
					{
						if (!String.IsNullOrEmpty(item.UpdatedByUserID) && 
							!vals.ContainsKey(item.UpdatedByUserID.Trim())) { vals.Add(item.UpdatedByUserID.Trim(), item.UpdatedByUserID.Trim()); }
					}
					break;
			}

			List<string> rtv = new List<string>();
			rtv.AddRange(vals.Keys);
