		/// <summary>
		/// Get the read elements as an XML list of strings.
		/// Strings are broken at 6000 characters.
		/// </summary>
		/// <param name="newElements">A List&lt;&gt; of strings (passed by reference) into which the method can insert elements marked as "new".</param>
		/// <param name="readElements">A List&lt;&gt; of strings (passed by reference) into which the method can insert elements marked as "read".</param>
		/// <param name="changedElements">A List&lt;&gt; of strings (passed by reference) into which the method can insert elements marked as "changed".</param>
		/// <param name="deletedElements">A List&lt;&gt; of strings (passed by reference) into which the method can insert elements marked as "deleted".</param>
		public void GetXMLChanges(ref List<string> newElements,
			ref List<string> readElements,
			ref List<string> changedElements,
			ref List<string> deletedElements)
		{
			StringBuilder sbNew = new StringBuilder(mgModel_BaseObject.SplitOnXMLHeader);
			StringBuilder sbRead = new StringBuilder(mgModel_BaseObject.SplitOnXMLHeader);
			StringBuilder sbChanged = new StringBuilder(mgModel_BaseObject.SplitOnXMLHeader);
			StringBuilder sbDeleted = new StringBuilder(mgModel_BaseObject.SplitOnXMLHeader);

			// Clear out the collections
			newElements = new List<string>();
			readElements = new List<string>();
			changedElements = new List<string>();
			deletedElements = new List<string>();

			// Go through the records and take care of them
			foreach (CustomDictionary item in this)
			{
				switch (item.RecordStatus)
				{
					case RecordStatus.Current:
						// Don't forget the to string
						sbRead.Append("<CustomDictionary>" + Environment.NewLine);
						sbRead.Append("\t<WordGUID>" + item.WordGUID + "</WordGUID>" + Environment.NewLine);
						sbRead.Append("\t<Word>" + item.Word + "</Word>" + Environment.NewLine);
						sbRead.Append("\t<DateAdded>" + item.DateAdded.ToString() + "</DateAdded>" + Environment.NewLine);
						sbRead.Append("\t<AddedByUserID>" + (item.AddedByUserID != null ? item.AddedByUserID : "NULL") + "</AddedByUserID>" + Environment.NewLine);
						sbRead.Append("\t<DateUpdated>" + item.DateUpdated.ToString() + "</DateUpdated>" + Environment.NewLine);
						sbRead.Append("</CustomDictionary>" + Environment.NewLine);

						if (sbRead.ToString().Trim().Length > mgModel_BaseObject.SplitOnXMLCharactersForLogging) 
						{ 
							readElements.Add(sbRead.ToString());
							sbRead = new StringBuilder(mgModel_BaseObject.SplitOnXMLHeader); 
						}
						break;

					case RecordStatus.Deleted:
						sbDeleted.Append("<CustomDictionary>" + Environment.NewLine);
						sbDeleted.Append("\t<WordGUID>" + item.WordGUID + "</WordGUID>" + Environment.NewLine);
						sbDeleted.Append("\t<Word>" + item.Word + "</Word>" + Environment.NewLine);
						sbDeleted.Append("\t<DateAdded>" + item.DateAdded.ToString() + "</DateAdded>" + Environment.NewLine);
						sbDeleted.Append("\t<AddedByUserID>" + (item.AddedByUserID != null ? item.AddedByUserID : "NULL") + "</AddedByUserID>" + Environment.NewLine);
						sbDeleted.Append("\t<DateUpdated>" + item.DateUpdated.ToString() + "</DateUpdated>" + Environment.NewLine);
						sbDeleted.Append("</CustomDictionary>" + Environment.NewLine);

						if (sbDeleted.ToString().Trim().Length > mgModel_BaseObject.SplitOnXMLCharactersForLogging)
						{
							deletedElements.Add(sbDeleted.ToString());
							sbDeleted = new StringBuilder(mgModel_BaseObject.SplitOnXMLHeader);
						}
						break;

					case RecordStatus.New:
						sbNew.Append("<CustomDictionary>" + Environment.NewLine);
						sbNew.Append("\t<WordGUID>" + item.WordGUID + "</WordGUID>" + Environment.NewLine);
						sbNew.Append("\t<Word>" + item.Word + "</Word>" + Environment.NewLine);
						sbNew.Append("\t<DateAdded>" + item.DateAdded.ToString() + "</DateAdded>" + Environment.NewLine);
						sbNew.Append("\t<AddedByUserID>" + (item.AddedByUserID != null ? item.AddedByUserID : "NULL") + "</AddedByUserID>" + Environment.NewLine);
						sbNew.Append("\t<DateUpdated>" + item.DateUpdated.ToString() + "</DateUpdated>" + Environment.NewLine);
						sbNew.Append("</CustomDictionary>" + Environment.NewLine);

						if (sbNew.ToString().Trim().Length > mgModel_BaseObject.SplitOnXMLCharactersForLogging)
						{
							newElements.Add(sbNew.ToString());
							sbNew = new StringBuilder(mgModel_BaseObject.SplitOnXMLHeader);
						}
						break;

					case RecordStatus.Modified:
						sbChanged.Append(item.GetChangedElementsAsXML() + Environment.NewLine);

						if (sbChanged.ToString().Trim().Length > mgModel_BaseObject.SplitOnXMLCharactersForLogging)
						{
							changedElements.Add(sbChanged.ToString());
							sbChanged = new StringBuilder(mgModel_BaseObject.SplitOnXMLHeader);
						}
						break;

				}
			}

			// Add the final strings to the list
			newElements.Add(sbNew.ToString());
			readElements.Add(sbRead.ToString());
			deletedElements.Add(sbDeleted.ToString());
			changedElements.Add(sbChanged.ToString());
		}

		/// <summary>
		/// Load the XML into a collection based on what's passed in.
		/// </summary>
		/// <param name="xml">The XML to process.</param>
		/// <returns>The completed CustomDictionaryCollection object.</returns>
		public static CustomDictionaryCollection LoadFromXML(string xml)
		{
			CustomDictionaryCollection coll = new CustomDictionaryCollection();
			System.Xml.XmlDocument doc = new System.Xml.XmlDocument();
			doc.LoadXml(xml);
			// <CustomDictionaryCollection>
			foreach (System.Xml.XmlNode nodeMain in doc.ChildNodes)
			{
				// <CustomDictionary>
				foreach (System.Xml.XmlNode node in nodeMain.ChildNodes)
				{
					// These are the elements <DetailGUID>, <UserName>, etc.
					CustomDictionary item = CustomDictionary.LoadFromXML(node.OuterXml);
					if (item != null) { coll.Add(item); }
				}
			}
			return coll;
		}
